// We need to input the library to read json files
import groovy.json.JsonSlurper
//we need to store the response
def response = context.testCase.getTestStepByName("Get All Product").getPropertyValue("Response")
//Stripping the response into a json script
def holder = new JsonSlurper().parseText(response)
def count = holder.count.toInteger()
for (def i=0;i<count;i++)
{
		if(holder.products[i].name=="HP CR 003")
		{
			def id = holder.products[i]._id
			log.info id
			def id1 = id.toString()
			context.testCase.testSuite.setPropertyValue("idp",id1)
			context.testCase.setPropertyValue("idp",id1)

			context.testCase.testSuite.project.setPropertyValue("idp",id1)
		}
}

// We need to input the library to read json files
import groovy.json.JsonSlurper
//we need to store the response
def response = context.testCase.getTestStepByName("Get All Orders").getPropertyValue("Response")
//Stripping the response into a json script
def holder = new JsonSlurper().parseText(response)
def count = holder.count.toInteger()
for (def i=0;i<count;i++)
{
		if(holder.orders[i].quantity=="20")
		{
			def id = holder.orders[i]._id
			log.info id
			def id1 = id.toString()
			context.testCase.testSuite.setPropertyValue("idp",id1)
			context.testCase.setPropertyValue("idp",id1)

			context.testCase.testSuite.project.setPropertyValue("idp",id1)
		}
}
